import React from 'react';
import '../formulario.css';
import SwContext from '../swContex';

function Formulario() {
  return (
    <SwContext.Consumer>
      { ({
        getName,
        getColuna,
        getOperador,
        getOperando,
        getOrdenador,
        getOrdem,
        operando,
        getFilter,
        opColuna,
      }) => (
        <form>
          <input
            type="text"
            name="setName"
            style={ {
              backgroundColor: 'green',
              color: 'blue',
            } }
            onChange={ getName }
            data-testid="name-filter"
          />
          <br />
          <label htmlFor="coluna">
            Coluna
            <select
              name="coluna"
              style={ {
                backgroundColor: 'green',
                color: 'blue',
              } }
              onChange={ getColuna }
              data-testid="column-filter"
            >
              { opColuna.map((item, index) => (
                <option
                  value={ item }
                  key={ index }
                >
                  { item }
                </option>))}
            </select>
          </label>
          <label htmlFor="operador">
            Operador
            <select
              name="operador"
              style={ {
                backgroundColor: 'green',
                color: 'blue',
              } }
              onChange={ getOperador }
              data-testid="comparison-filter"
            >
              <option value="maior que" selected>
                maior que
              </option>
              <option value="menor que">
                menor que
              </option>
              <option value="igual a">
                igual a
              </option>
            </select>
          </label>
          <input
            type="number"
            onChange={ getOperando }
            value={ operando }
            style={ {
              backgroundColor: 'green',
              color: 'blue',
            } }
            min="0"
            data-testid="value-filter"
          />
          <button
            type="button"
            data-testid="button-filter"
            onClick={ getFilter }
            style={ {
              backgroundColor: 'green',
              color: 'blue',
            } }
          >
            FILTRAR
          </button>
          <br />
          <label
            htmlFor="paran"
          >
            Ordenar
            <select
              name="paran"
              style={ {
                backgroundColor: 'green',
                color: 'blue',
              } }
              onChange={ getOrdenador }
            >
              <option value="population">
                population
              </option>
              <option value="orbital_period">
                orbital_period
              </option>
              <option value="diameter">
                diameter
              </option>
              <option value="rotation_pedriod">
                rotation_pedriod
              </option>
              <option value="surface_water">
                surface_water
              </option>
            </select>
          </label>
          <label
            htmlFor="ordem"
            onChange={ getOrdem }

          >
            <input
              type="radio"
              name="ordem"
              value="Ascendente"
              style={ {
                backgroundColor: 'green',
                color: 'blue',
              } }
            />
            Ascendente
            <input
              type="radio"
              name="ordem"
              value="Descendente"
              style={ {
                backgroundColor: 'green',
                color: 'blue',
              } }
            />
            Descendente
          </label>
          <button
            type="button"
            style={ {
              backgroundColor: 'green',
              color: 'blue',
            } }
          >
            ORDENAR
          </button>
        </form>)}
    </SwContext.Consumer>
  );
}

export default Formulario;
