import React from 'react';
import SwContext from '../swContex';

function Table() {
  return (
    <SwContext.Consumer>
      { ({
        data,
        filterByNumericValues,
        getOpColuna,
      }) => (
        <div className="table-wrapper">
          <fieldset>
            Filtros
            {
              filterByNumericValues.map((planet, index) => (
                <div
                  key={ index }
                  data-testid="filter"
                >
                  { planet.column }
                  {' '}
                  { planet.comparison }
                  {' '}
                  { planet.value }
                  <button
                    type="button"
                    data-testid="button-remove-filters"
                    onClick={ getOpColuna }
                  >
                    x
                  </button>
                </div>
              ))
            }
          </fieldset>
          <br />
          <table>
            <thead>
              <tr className="topo">
                <th>Name</th>
                <th>Rotation Period</th>
                <th>Orbital Pedriod</th>
                <th>Diameter</th>
                <th>Climate</th>
                <th>Gravity</th>
                <th>Terrain</th>
                <th>Surfece Water</th>
                <th>Population</th>
                <th>Films</th>
                <th>Created</th>
                <th>Edited</th>
                <th>URL</th>
              </tr>
            </thead>
            <tbody>
              {
                data.map((planet, index) => (
                  <tr key={ index }>
                    <td>{ planet.name }</td>
                    <td>{ planet.rotation_period }</td>
                    <td>{ planet.orbital_period }</td>
                    <td>{planet.diameter}</td>
                    <td>{ planet.climate }</td>
                    <td>{ planet.gravity }</td>
                    <td>{ planet.terrain }</td>
                    <td>{ planet.surface_water }</td>
                    <td>{ planet.population }</td>
                    <td>{ planet.films }</td>
                    <td>{ planet.created }</td>
                    <td>{ planet.edited }</td>
                    <td>{ planet.url }</td>
                  </tr>
                ))
              }
            </tbody>
          </table>
        </div>
      ) }
    </SwContext.Consumer>

  );
}

export default Table;
