import React, { useEffect, useState } from 'react';
import PropTypes from 'prop-types';
import swPlanetsApi from './componetes/api';
import SwContext from './swContex';

function StarWarProvider({ children }) {
  const [data, setData] = useState([]);
  const [name, setName] = useState('');
  const [coluna, setColuna] = useState('');
  const [operador, setOperador] = useState('');
  const [operando, setOperando] = useState(0);
  const [ordenador, setOrdenador] = useState('');
  const [ordem, setOrdem] = useState('');

  async function getData() {
    const temp = await swPlanetsApi();
    setData(temp.results);
  }

  useEffect(() => {
    getData();
  }, []);

  function getColuna(target) {
    const { value } = target;
    setColuna(value);
  }

  function getOperador(target) {
    const { value } = target;
    setOperador(value);
  }

  function getOperando(target) {
    const { value } = target;
    setOperando(value);
  }

  function getOrdenador(target) {
    const { value } = target;
    setOrdenador(value);
  }

  function getOrdem(target) {
    const { value } = target;
    setOrdem(value);
  }

  /* function getName() {
    const valor = value;
    console.log(valor);
  } */

  function handleChange({ target }) {
    const { name } = target;
    const value = target.type === 'checkbox' ? target.checked : target.value;
    console.log(value);
  }

  const contextValue = {
    data,
    coluna,
    operador,
    operando,
    ordenador,
    ordem,
    name,
    /* getName, */
    getColuna,
    getOperador,
    getOperando,
    getOrdenador,
    getOrdem,
    handleChange,
  };

  return (
    <SwContext.Provider value={ contextValue }>
      { children }
    </SwContext.Provider>
  );
}

StarWarProvider.propTypes = {
  children: PropTypes.objectOf.isRequired,
};

export default StarWarProvider;
