import React, { useEffect, useState } from 'react';
import PropTypes from 'prop-types';
import swPlanetsApi from './componetes/api';
import SwContext from './swContex';

function StarWarProvider({ children }) {
  const [data, setData] = useState([]);
  const [restoryData, setRestoryData] = useState([]);
  const [filterByName, setName] = useState('');
  const [filterByNumericValues, setFilter] = useState([]);
  const [coluna, setColuna] = useState('');
  const [operador, setOperador] = useState('');
  const [operando, setOperando] = useState(0);
  const [ordenador, setOrdenador] = useState('');
  const [ordem, setOrdem] = useState('');
  const [opColuna, setOpColuna] = useState([
    'population',
    'orbital_period',
    'diameter',
    'rotation_period',
    'surface_water',
  ]);

  async function getData() {
    const temp = await swPlanetsApi();
    setData(temp.results);
    setColuna('population');
    setOperador('maior que');
    setRestoryData(temp.results);
  }

  useEffect(() => {
    getData();
  }, []);

  function filterData() {
    const temp = restoryData.filter((planet) => planet.name.includes(filterByName));
    setData(temp);
  }

  function filter() {
    if (filterByName.length !== 0) { filterData(); } else { getData(); }
  }

  useEffect(() => {
    filter();
  // eslint-disable-next-line react-hooks/exhaustive-deps
  }, [filterByName]);

  function colun(planeta) {
    if (coluna === 'population') return (planeta.population);
    if (coluna === 'orbital_period') return (planeta.orbital_period);
    if (coluna === 'diameter') return (planeta.diameter);
    if (coluna === 'rotation_period') return (planeta.rotation_period);
    if (coluna === 'surface_water') return (planeta.surface_water);
    console.log('colun');
  }

  function maior() {
    setData(data.filter((planeta) => (colun(planeta)) > +operando));
  }

  function menor() {
    setData(data.filter((planeta) => (colun(planeta)) < +operando));
  }

  function igual() {
    setData(data.filter((planeta) => (colun(planeta)) === operando));
  }

  async function filtrar() {
    if (operador === 'maior que') maior();
    if (operador === 'menor que') menor();
    if (operador === 'igual a') igual();
    console.log('filtrar');
  }

  function getColuna({ target }) {
    const valor = target.value;
    setColuna(valor);
  }

  function getOperador({ target }) {
    const valor = target.value;
    setOperador(valor);
  }

  function getOperando({ target }) {
    const valor = target.value;
    setOperando(valor);
  }

  function getOrdenador({ target }) {
    const valor = target.value;
    setOrdenador(valor);
  }

  function getOrdem({ target }) {
    const valor = target.value;
    setOrdem(valor);
  }

  function getName({ target }) {
    const valor = target.value;
    setName(valor);
  }

  function getOpColuna() {
    const usados = filterByNumericValues.map((item) => item.column);
    const valor = opColuna.filter((item) => !usados.includes(item));
    console.log(usados);
    console.log(valor);
    /* setOpColuna(temp); */
  }

  async function getFilter() {
    const valor = {
      column: coluna,
      comparison: operador,
      value: operando,
    };
    const temp = [...filterByNumericValues, valor];
    console.log('getFilter');
    setFilter(temp);
    filtrar();
  }

  const contextValue = {
    data,
    coluna,
    operador,
    operando,
    ordenador,
    ordem,
    filterByName,
    filterByNumericValues,
    opColuna,
    getFilter,
    getName,
    getColuna,
    getOperador,
    getOperando,
    getOrdenador,
    getOrdem,
    filter,
    setFilter,
    filtrar,
    getOpColuna,
  };

  return (
    <SwContext.Provider value={ contextValue }>
      { children }
    </SwContext.Provider>
  );
}

StarWarProvider.propTypes = {
  children: PropTypes.objectOf.isRequired,
};

export default StarWarProvider;
